import React from 'react'
import { Avatar, Image, Table, Tag } from 'antd'
import { BiSolidUser } from 'react-icons/bi'
import { formatCurrency } from '../context/utils'
import moment from 'moment'
import { ColumnsType } from 'antd/es/table'

type Props = {
  data?: any,
  column?: any,
  rowKey?: string,
  hidden?: boolean,
  noHeader?: boolean,
  loading?: boolean,
  position?: string,
  pagination?: any,
  rowSelection?: any,
}
function CustomTable({
  data,
  column,
  rowKey,
  loading,
  position,
  noHeader,
  pagination,
  rowSelection,
  hidden=false,
}: Props) {
  const altColumn: ColumnsType<any> = [
    {
      title: 'User',
      render: (data) => (<div className='flex space-x-5 items-center'>
        <Avatar
          size="large"
          src={data?.img}
          alt={data?.name}
          className='text-white'
          icon={<BiSolidUser className='m-auto mt-1.5' />}
        />
        <p className='text-lint'>
          {data?.name || "full name"}
        </p>
      </div>)
    },
    {
      title: 'Item',
      render: (data) => (<div className='flex space-x-5 items-center'>
        <Image
          src={data?.img}
          preview={false}
          alt={data?.name}
          className='!w-[50px]'
          fallback="data:image/png;base64,"
        />
        <p className='text-lint'>
          {data?.name || "product name"}
        </p>
      </div>)
    },
    {
      title: 'Price',
      render: (data) => (<div className='flex space-x-1 text-lint'>
        <p>{formatCurrency(data?.amount || 12.26)}</p>
        <p>({data?.bids || "706"})</p>
      </div>)
    },
    {
      title: 'Date',
      dataIndex: 'date',
      render: (data) => (<p>{moment(data || new Date()).format('L')}</p>)
    },
    {
      title: 'Matched',
      render: (data) => (<Tag className='rounded-xl border-0 bg-light px-3 py-1'>{`${data?.from || 2} Out Of ${data?.to || 5}`}</Tag>)
    }
  ]

  const altData = [
    {},
    {},
    {},
    {},
    {},
  ]
  return (
    <div className='w-full whitespace-nowrap overflow-x-auto' hidden={hidden}>
      <Table
        key={rowKey}
        rowKey={rowKey}
        loading={loading}
        showHeader={!noHeader}
        rowSelection={rowSelection}
        className='min-w-full relative'
        dataSource={data || [] || altData}
        columns={column || [] || altColumn}
        pagination={pagination && { pageSize: pagination || 5, position: [position || 'bottomCenter'] }}
      />
    </div>
  )
}

export default CustomTable